import React from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import CssBaseline from "@material-ui/core/CssBaseline";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Divider from "@material-ui/core/Divider";
import { useDropzone } from "react-dropzone";
import RootRef from "@material-ui/core/RootRef";
import { makeStyles } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";
import { green } from "@material-ui/core/colors";
import Button from "@material-ui/core/Button";
import Fab from "@material-ui/core/Fab";
import CheckIcon from "@material-ui/icons/Check";
import CloudUpload from "@material-ui/icons/CloudUpload";
import clsx from "clsx";
import { LinearProgress } from "@material-ui/core";
import axios from "axios";

const useStyles = makeStyles((theme) =>({
  dropZoneContainer:{
    height:300,
    background:"#efefef",
    display:'flex',
    alignItems:'center',
    justifyContent:"center",
    borderStyle:"dashed",
    borderColor:"#aaa"
  },

  preview: {
    width: 250,
    height: 250,
    margin: "auto",
    display: "block",
    marginBottom: theme.spacing(2),
    objectFit: "contain",
  },
  wrapper: {
    margin: theme.spacing(1),
    position: "relative",
  },
  buttonSuccess: {
    backgroundColor: green[500],
    "&:hover": {
      backgroundColor: green[700],
    },
  },
  fabProgress: {
    color: green[500],
    position: "absolute",
    top: -6,
    left: -6,
    zIndex: 1,
  },
  buttonProgress: {
    color: green[500],
    position: "absolute",
    top: "50%",
    left: "50%",
    marginTop: -12,
    marginLeft: -12,
  },


}))

function App() {

const classes = useStyles();
const [loading, setLoading] = React.useState(false);
const [success, setSuccess] = React.useState(false);
const [file, setFile] = React.useState();
const [preview, setPreview] = React.useState();
const [percent, setPercent] = React.useState(0);
const [downloadUri, setDownloadUri] = React.useState();
const [selectedImageFile, setSelectedImageFile] = React.useState();

const buttonClassname = clsx({
  [classes.buttonSuccess]: success,
});

const onDrop=React.useCallback((acceptedFiles) => {
  console.log(acceptedFiles);
  setFile(acceptedFiles[0])
  const previewUrl = URL.createObjectURL(acceptedFiles[0])
  setPreview(previewUrl);
      
});

const {getRootProps,getInputProps} = useDropzone({
multiple:false,
onDrop,
});

const {ref,...rootProps} = getRootProps()


const uploadFile = async () => {
  try {
    setSuccess(false);
    setLoading(true);
    const formData = new FormData();
    formData.append("file", file);
    const API_URL = "http://localhost:8080/files";
    const response = await axios.put(API_URL, formData, {
      onUploadProgress: (progressEvent) => {
        const percentCompleted = Math.round(
          (progressEvent.loaded * 100) / progressEvent.total
        );
        setPercent(percentCompleted);
      },
    });

    setDownloadUri(response.data.fileDownloadUri);
    setSuccess(true);
    setLoading(false);
  } catch (err) {
    alert(err.message);
  }
};

  return (
  <>
    < CssBaseline />
    <AppBar position="fixed">

    <Toolbar>
      <Typography variant="h6">React File Upload</Typography>


    </Toolbar>

      </AppBar>

      <Toolbar />
      <Toolbar />

      <Container maxWidth="md">
        <Paper elevation={4}>
          <Grid container>
            <Grid item xs={12}>
              <Typography align="centre" style={{padding:16}}>
                File Upload
              </Typography>
              <Divider />
              </Grid>
          
        <Grid item xs={6} style={{padding:16}} >
          <RootRef rootRef={ref}>
          <Paper 
          {...rootProps}
          elevation={0}
          className={classes.dropZoneContainer}
          >
            <input {...getInputProps()}/>
            <p> Drag n Drop</p>
          </Paper>
          </RootRef>
          
        </Grid>

        <Grid item xs={6} style={{padding:16}}>
          <Typography align="center" variant="subtitle1">
            Preview
          </Typography>
          <img 
          onLoad={() => URL.revokeObjectURL(preview)}
          className={classes.preview}
          src={preview || "http://via.placeholder.com/250"}
          /> 

      {/* */}
     {file && <>
      
        <Divider />
        <Grid conatiner style={{marginTop:16}} alignItems="center">
          <Grid item xs={2}>
          <div className={classes.wrapper}>
                        <Fab
                          aria-label="save"
                          color="primary"
                          className={buttonClassname}
                          onClick={uploadFile}
                        >
                          {success ? <CheckIcon /> : <CloudUpload />}
                        </Fab>
                        {loading && (
                          <CircularProgress
                            size={68}
                            className={classes.fabProgress}
                          />
                        )}
                      </div>
            </Grid>
            <Grid item xs={10}>
            {file &&  ( <Typography variant="body">{file.name}</Typography>)}
              {loading && (<div>
                <LinearProgress variant="determinate" value={percent} />
                <div style={{
                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                            }}>
                  <Typography variant="body"> {percent}% </Typography>
                </div>
              </div>)}

              {success && (
                <Typography> 
                File Upload Success!{" "}
                <a href={downloadUri} target="_blank">
                  File Url
               </a>
               </Typography>

)}


            </Grid>



        </Grid>
        </>
}
        {/* */}
        </Grid>
          </Grid>
        </Paper>
      </Container>


  </>
  
  );
}

export default App;
